/*Creamos un nuevo Marker para que cada vez que aparezca un
nuevo marker, aparezca el popup*/
const CustomMarker = props => {
  const initMarker = ref => {
    if (ref) {
      ref.leafletElement.openPopup()
    }
  }
  return <Marker ref={initMarker} {...props}/>
}

